import ImagePlane from "../ImagePlane/ImagePlane";
import { Suspense, useRef } from "react";
import img1 from "../../assets/image/img1.webp";
import img2 from "../../assets/image/img2.webp";
import img3 from "../../assets/image/img3.webp";
import img4 from "../../assets/image/img4.webp";
import img5 from "../../assets/image/img5.webp";
import img6 from "../../assets/image/img6.webp";
import img7 from "../../assets/image/img7.webp";
import img8 from "../../assets/image/img8.webp";
import img9 from "../../assets/image/img9.webp";
import img10 from "../../assets/image/img10.webp";
import { IMAGE_BLOCK_WIDTH, IMAGE_GAP } from "../../utils/utilFormat";
import { useFrame, useThree } from "@react-three/fiber";
import { useStore } from "../../store/store";
const Scene = () => {
  const imageGroupRef = useRef();
  const { scrollSpeed, setScrollSpeed } = useStore();
  const imagesArr = [
    img1,
    img2,
    img3,
    img4,
    img5,
    img6,
    img7,
    img8,
    img9,
    img10,
    img1,
    img2,
    img3,
    img4,
    img5,
    img6,
    img7,
    img8,
    img9,
    img10,
    img1,
    img2,
    img3,
    img4,
    img5,
    img6,
    img7,
    img8,
    img9,
    img10,
  ];
  const numImages = imagesArr.length;
  const leftBoundary = 0;
  const rightBoundary = -(
    (numImages - 1) * IMAGE_BLOCK_WIDTH +
    (numImages - 1) * IMAGE_GAP
  );

  useFrame((state, delta) => {
    if (imageGroupRef.current) {
      console.log(scrollSpeed);
      const { x, y, z } = imageGroupRef.current.position;
      if (x - scrollSpeed <= rightBoundary) {
        imageGroupRef.current.position.set(rightBoundary, y, z);
        setScrollSpeed(0);
        return;
      } else if (x - scrollSpeed >= leftBoundary) {
        imageGroupRef.current.position.set(leftBoundary, y, z);
        setScrollSpeed(0);
        return;
      }
      // imageGroupRef.current.position.set(x - scrollSpeed, y, z);
      setScrollSpeed(0);
    }
  });
  return (
    <Suspense fallback={null}>
      <group ref={imageGroupRef}>
        {imagesArr.map((url, index) => {
          const imagePosition = [
            IMAGE_BLOCK_WIDTH * index + IMAGE_GAP * index,
            0,
            0,
          ];
          return (
            <ImagePlane key={index} imgUrl={url} position={imagePosition} />
          );
        })}
      </group>
    </Suspense>
  );
};

export default Scene;
