import ImagePlane from "../ImagePlane/ImagePlane";
import { Suspense } from "react";

import { IMAGE_BLOCK_WIDTH, IMAGE_GAP } from "../../utils/utilFormat";
import { useThree } from "@react-three/fiber";
import { ScrollControls, Scroll } from "@react-three/drei";
import { imagesArr } from "../../utils/utilFormat";

const Scene = () => {
  const imgWidth = 0.65;
  const imgGap = 0.15;
  const numImages = imagesArr.length;
  const { width } = useThree((state) => state.viewport);
  const itemsLength = width + (numImages - 1) * (imgWidth + imgGap);
  return (
    <Suspense fallback={null}>
      <ScrollControls horizontal damping={5} pages={itemsLength / width}>
        <Scroll>
          {imagesArr.map((url, index) => {
            const imagePosition = [imgWidth * index + imgGap * index, 0, 0];
            return (
              <ImagePlane
                key={index}
                index={index}
                imgUrl={url}
                position={imagePosition}
                scale={[imgWidth, 4, 1]}
              />
            );
          })}
        </Scroll>
      </ScrollControls>
    </Suspense>
  );
};

export default Scene;
