import { useTexture } from "@react-three/drei";
import { Suspense, useState, useRef } from "react";
import { IMAGE_BLOCK_HEIGHT, IMAGE_BLOCK_WIDTH } from "../../utils/utilFormat";
import { useScroll } from "@react-three/drei";
import { useFrame } from "@react-three/fiber";
import * as THREE from "three";
import { imagesArr } from "../../utils/utilFormat";
const ImagePlane = ({
  index,
  imgUrl,
  position = [0, 0, 0],
  scale = [1, 1, 1],
  color = new THREE.Color(),
  ...props
}) => {
  const imgRef = useRef();
  const [hover, setHover] = useState(false);
  const [click, setClick] = useState(false);
  const [imgTexture] = useTexture([imgUrl]);
  imgTexture.repeat.set(
    (65 * imgTexture.image.height) / (imgTexture.image.width * 250),
    1
  );
  imgTexture.offset.set(0.42, 0);
  const numImages = imagesArr.length;
  const scroll = useScroll();
  useFrame((state, delta) => {
    const y = scroll.curve(index / numImages - 1.5 / numImages, 4 / numImages);
  });
  return (
    <mesh
      position={position}
      onPointerOver={() => setHover(true)}
      onPointerOut={() => setHover(false)}
    >
      <planeGeometry args={[IMAGE_BLOCK_WIDTH, IMAGE_BLOCK_HEIGHT]} />
      <meshBasicMaterial color="#666666" map={imgTexture} />
    </mesh>
  );
};

export default ImagePlane;
