import React from "react";
import { Suspense } from "react";
import { Canvas } from "@react-three/fiber";
import "./App.scss";
import Scene from "./components/Scene/Scene";
import Home from "./components/Home/Home";
import normalizeWheel from "normalize-wheel";
import { useStore } from "./store/store";
const App = () => {
  const { setScrollSpeed, setScrollDirection } = useStore();
  //left > 0
  //right < 0
  //top > 0
  //bottom < 0
  const moveTileOnWheel = (mouse) => {
    const { pixelX, pixelY } = normalizeWheel(mouse);
    let curDirection = "L";
    console.log(pixelY, "test");
    if (pixelX < 0) {
      curDirection = "R";
    } else if (pixelX > 0) {
      curDirection = "L";
    } else if (pixelY > 0) {
      curDirection = "L";
    } else {
      curDirection = "R";
    }
    const speed = Math.max(Math.abs(pixelX), Math.abs(pixelY));
    setScrollDirection(curDirection);
    setScrollSpeed(speed);
  };
  return (
    <>
      <div
        className="w-100 h-100 position-relative canvas-container remove-canvas-scroll-bar"
        onWheel={moveTileOnWheel}
      >
        <Suspense fallback={null}>
          <Canvas
            linear
            orthographic
            colorManagement
            dpr={[1, 1.5]}
            gl={{
              antialias: true,
            }}
          >
            <Scene />
          </Canvas>
        </Suspense>
      </div>

      <Home />
    </>
  );
};

export default App;
