import { useTexture } from "@react-three/drei";
import { Suspense } from "react";

const ImagePlane = ({ imgUrl }) => {
  const [imgTexture] = useTexture([imgUrl]);
  imgTexture.repeat.set(70 / (imgTexture.image.width * 250), 1);
  console.log(imgTexture.image.height, imgTexture.image.width);
  return (
    <Suspense fallback={null}>
      <mesh>
        <planeGeometry args={[700, 2500]} />
        <meshBasicMaterial color="#ffffff" map={imgTexture} />
      </mesh>
    </Suspense>
  );
};

export default ImagePlane;
