import * as THREE from "three";
import React, { useRef, useEffect, Suspense } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import "./App.css";
import Scene from "./components/Scene/Scene";
import { useStore } from "./store/store";
import normalizeWheel from "normalize-wheel";
const App = () => {
  const { setScrollSpeed } = useStore();
  const updateMouseSpeed = (e) => {
    const normalizedWheel = normalizeWheel(e);
    const { pixelX, pixelY } = normalizedWheel;
    console.log(pixelX, pixelY);
    // right: pixelX is negative
    // left: pixelX is positive
    // top: pixelY is positive
    // bottom: pixelY is negative

    //top and left move same direction
    //bottom and right move same direction
    let positive = true;
    let scrollSpeedVal = 0;
    if (pixelX < 0) {
      positive = false;
    } else if (pixelX === 0 && pixelY < 0) {
      positive = false;
    }
    const speedVal = Math.max(Math.abs(pixelX), Math.abs(pixelY));
    scrollSpeedVal = positive ? speedVal : -speedVal;
    // console.log(scrollSpeedVal);
    setScrollSpeed(scrollSpeedVal);
  };
  return (
    <div onWheel={updateMouseSpeed} className="app">
      <Canvas
        linear
        orthographic
        colorManagement
        camera={{ position: [0, 0, 8] }}
        dpr={Math.max(window.devicePixelRatio, 2)}
        gl={{
          antialias: true,
          alpha: false,
          shadowMap: true,
        }}
      >
        <axesHelper />
        {/* <Scene /> */}
      </Canvas>
    </div>
  );
};

export default App;
