import * as THREE from "three";
import React, { useRef, useEffect } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { useTexture } from "@react-three/drei";
import "./App.css";
import ImagePlane from "./components/ImagePlane/ImagePlane";
import img1 from "./assets/image/img1.jpg";
import img2 from "./assets/image/img2.jpg";
import img3 from "./assets/image/img3.jpg";
import img4 from "./assets/image/img4.jpg";

const App = () => {
  const [img1Texture, img2Texture, img3Texture, img4Texture] = useTexture([
    img1,
    img2,
    img3,
    img4,
  ]);
  return (
    <>
      <Canvas linear orthographic camera={{ position: [0, 0, 500] }}>
        <ImagePlane imgTexture={img1Texture} />
        <ImagePlane imgTexture={img2Texture} />
        <ImagePlane imgTexture={img3Texture} />
        <ImagePlane imgTexture={img4Texture} />
      </Canvas>
    </>
  );
};

export default App;
