import * as THREE from "three";
import React, { useRef, useEffect, Suspense } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import "./App.css";
import Scene from "./components/Scene/Scene";
import { useStore } from "./store/store";
import normalizeWheel from "normalize-wheel";
const App = () => {
  const { setScrollSpeed } = useStore();
  const updateMouseSpeed = (e) => {
    const normalizedWheel = normalizeWheel(e);
    const { pixelX, pixelY } = normalizedWheel;
    console.log(pixelX, pixelY);
    // const positive = pi
    // setScrollSpeed(normalizeWheel(e).pixelX);
  };
  return (
    <div onWheel={updateMouseSpeed} className="app">
      <Canvas
        linear
        orthographic
        colorManagement
        camera={{ position: [0, 0, 8] }}
        dpr={Math.max(window.devicePixelRatio, 2)}
        gl={{
          antialias: true,
          alpha: false,
          shadowMap: true,
        }}
      >
        <Scene />
      </Canvas>
    </div>
  );
};

export default App;
